
public class maths {

	public static void main(String[] args) {
		// Declaration of the operands
		int a=-3;
		int b=4;
		
		// To find maximum of two numbers
		System.out.println("Maximum number "+Math.max(a, b));
		
		// To find minimum of two numbers
	    System.out.println("Minimum number "+Math.min(a, b));
		
	 // To find square root number
	 		System.out.println("Square root "+Math.sqrt(b));
	 // To generate a random number
		System.out.println("Random number "+Math.random());
	// To find absolute value of a number
		System.out.println("Absolute value "+Math.abs(a));
						
	 		
				

	}

}
