
public class typecasting {

	public static void main(String[] args) {
		// wide typecasting
		int no=6;
		float s=no;
		// display
		System.out.println("Integer Value "+no);
		System.out.println("Typecasted float "+s);
		//narrow typecasting
		float m=(float)9.00;
		int y=(int) m;
		//display
		System.out.println("Float value "+m);
		System.out.println("Typecasted integer "+y);
		
		

	}

}
